function pageprocessorEvents(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
$("#fixedFooterContent").html('');
  $.blockUI();
  var url = pagedef.url;
  $scope.page = 1;
  var weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  var monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  var dayStr;
  var monthStr;
  var dateStr;
  var currDate;

  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": url
  };
  $http.post(rssreaderUrl, param, '').success(function(resp, status, headers, config) {
    $scope.feeds = resp.feed.entries;
    //$rootScope.feeds = data.feed.entries;
    var data = [];
    var index = 0;
    $.each($scope.feeds, function(key, val) {
      dayStr = "";
      monthStr = "";
      dateStr = "";
      if (val.pubDate) {
        currDate = new Date(val.pubDate);
        dayStr = weekDays[currDate.getDay()];
        monthStr = monthNames[currDate.getMonth()];
        dateStr = currDate.getDate();
      }
      data.push({
        "link": val.link,
        "displaydate": val.pubDate,
        //"formattedDate": moment(val.pubDate).format("ddd MMM DD"),
        "formattedDate": dayStr + " " + monthStr + " " + dateStr,
        "date": new Date(val.pubDate).getTime().toString(),
        // "currentDate" : new Date(val.pubDate),
        "title": val.title,
        "description": val.content,
        "index": key
      });
    });

    $rootScope.Eventsdata = data;
    $.unblockUI();

    $("#appContent").append($(pagedef.pageTemplate));
    $("#EventsListData").eventCalendar({
      jsonData: $rootScope.Eventsdata,
      showDescription: true,
      startWeekOnMonday: false,
      eventsLimit: 20,
      dataTemplate: pagedef.datatemplate,
      //cacheJson: false,
      moveSpeed: 0,
      moveOpacity: 0
    });
    $('.monthTitle').trigger('click');
    $('.arrow').click(function() {
      $('.monthTitle').trigger('click');
      setTimeout(function() {
        $('.eventsCalendar-loading').css('display', 'none');
        try {
          $scope.$apply();
        } catch (ex) {
          console.log("ex" + JSON.stringify(ex));
        }
      }, 500);
    });

    try {
      $scope.$apply();
    } catch (ex) {
      console.log(JSON.stringify(ex));
    } finally {
      console.log($('.monthTitle'));
    }
  }).error(function(error, status, header, config) {
    //alert(JSON.stringify(error));
    $.unblockUI();
  });

}

