                alert('Unable to get preferences.');
            }
            return;
        }

        $rootScope.category = data['_embedded']['category'];

        var flag = true;

        for (var i = 0; i < $rootScope.category.length; i++) {
            if ($rootScope.category[i].enable == true || $rootScope.category[i].rename == false) {
                flag = false;
                break;
            }
        }

        if (flag) {
            $scope.showInfo();
        }

    });


    $scope.showInfo = function () {
        if (window.device) {
            navigator.notification.alert(
                'Students can see Connect\'s post depending on categories selected.', // message
                null, // callback
                'This feature helps students to select categories.', // title
                'OK' // buttonName
            );
        } else {
            alert('This feature helps students to select categories.' + '\n' + 'Students can see Connect\'s post depending on categories selected');
        }

    };

    $scope.editCategory = function (index) {
        $rootScope.categoryIndex = index;
        $location.path('/app/Settings2/Settings2Page6');
    }


}

function pageprocessorSettings2Page6(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.category = $rootScope.category[$rootScope.categoryIndex];

    if (!$scope.category.notificationSetting.email)
        $scope.category.notificationSetting.email = false;

    if (!$scope.category.notificationSetting.override)
        $scope.category.notificationSetting.override = false;

    $scope.editMode = false;
    $scope.categoryLabel = $scope.category.category;
    $rootScope.appletName = "Manage Categories";
    $scope.email = '';
    $scope.isValid = false;
    var testMail = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;


    var flag = true;

    for (var i = 0; i < $rootScope.category.length; i++) {
        if ($rootScope.category[i].enable == true || $rootScope.category[i].rename == false) {
            flag = false;
            break;
        }
    }
    $scope.showInfo = function () {
        if (window.device) {
            navigator.notification.alert(
                'Only Connect\'s admin can create post based on category assigned.', // message
                null, // callback
                'This feature helps you to assign admin for a category.', // title
                'OK' // buttonName
            );
        } else {
            alert('This feature helps you to assign admin for a category.' + '\n' + 'Only Connect\'s admin can create post based on category assigned.');
        }

    };

    if (flag) {
        $scope.showInfo();
    }


    var url = '/unifyedcategory/category';

    $scope.$destroy = function () {
        $rootScope.category[$rootScope.categoryIndex] = $scope.category;
    };

    $scope.update = function (cb) {
        var categoryUrl = url + '/' + $scope.category['id'];

        $scope.callAPI(categoryUrl, 'PUT', $scope.category, function (res) {
            return cb(res);
        });

    };

    $scope.changeState = function () {


        if (flag && $scope.category.enable) {
            if (window.device) {
                navigator.notification.confirm(
                    "Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.",  // message
                    onConfirm,              // callback to invoke with index of button pressed
                    '',            // title
                    'Ok,Cancel'          // buttonLabels
                );
            } else if ($rootScope.webApp) {

                $.confirm({
                    animation: 'none',
                    icon: '',
                    title: '',
                    content: "Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.",
                    titleClass: 'proximaSemiBoldFont fontSize18 textcenter',
                    columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
                    buttons: {
                        CANCEL: {
                            'btnClass': 'btn-cancel boxradius popBtns',
                            'action': function () {
                                //alert('cancel');
                                $scope.category.enable = !$scope.category.enable;
                                $scope.$apply();
                            }
                        },
                        OK: {
                            'btnClass': 'btn-primary boxradius marginleft30px popBtns',
                            'action': function () {

                                $scope.category.rename = false;
                                $scope.update(function (res) {
                                    flag = false;
                                    $scope.registerUser();
                                });

                            }
                        }
                    }
                });
            } else {
                var r = confirm("Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.");
                if (r == true) {
                    $scope.category.rename = false;
                    $scope.update(function (res) {
                        flag = false;
                        $scope.registerUser();
                    });
                } else {
                    $scope.category.enable = !$scope.category.enable;
                    $scope.$apply();
                }
            }

        } else {
            $scope.category.rename = false;
            $scope.update(function (res) {
                $scope.registerUser();
            });
        }


        function onConfirm(buttonIndex) {
            if (buttonIndex == 1) {
                $scope.category.category = $scope.categoryLabel;
                $scope.category.rename = false;
                $scope.update(function (res) {
                    flag = false;
                    $scope.registerUser();
                });
            } else {
                $scope.category.enable = !$scope.category.enable;
                $scope.$apply();
            }
        }


    };
    $scope.openPrevFeed = function(){
        window.history.back();
    }
    $scope.toggleView = function () {
        if ($scope.category['rename'] && !$scope.category['enable']) {
            $scope.editMode = !$scope.editMode;
            $scope.categoryLabel = $scope.category.category;
        }
    };

    $('#addWhatsupAdminForm').on('submit', function (event) {
        event.preventDefault();

        $scope.$digest();

        $scope.add();


        return false;
    });


    $('#categoryRenameForm').on('submit', function (event) {
        event.preventDefault();

        $scope.$digest();

        $scope.category.category = $scope.categoryLabel;
        $scope.update(function (res) {
            $scope.editMode = !$scope.editMode;
        });


        return false;
    });

    $scope.emailChange = function () {
        $scope.isValid = testMail.test($scope.email);
    }

    $scope.remove = function (index) {
        if ($rootScope.webApp) {
            $.confirm({
                animation: 'none',
                icon: 'deleteIcon',
                title: 'Delete User',
                titleClass: 'proximaSemiBoldFont fontSize18 textcenter',
                content: 'Do you want to delete this user ?',
                columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
                buttons: {
                    CANCEL: {
                        'btnClass': 'btn-cancel boxradius popBtns',
                        'action': function () {
                            //alert('cancel');
                        }
                    },
                    DELETE: {
                        'btnClass': 'btn-primary boxradius marginleft30px popBtns',
                        'action': function () {

                            $scope.category.whatsupAdmins.splice(index, 1);
                            $scope.update(function (res) {

                            });
                        }
                    }
                }
            });
        } else {

            var userdelPost = function (btn) {
                if (btn == 1) {
                    $scope.category.whatsupAdmins.splice(index, 1);
                    $scope.update(function (res) {

                    });
                }
            }

            var options = {
                'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
                'title': '',
                'buttonLabels': ['Delete User', 'Cancel'],
                'addCancelButtonWithLabel': 'Cancel'
            };
            window.plugins.actionsheet.show(options, userdelPost);
        }


    }

    $scope.add = function () {
        if (!$scope.isValid) return;
        if ($scope.category.whatsupAdmins.indexOf($scope.email) !== -1) {
            if (window.device) {
                navigator.notification.alert(
                    'Email already exists.', // message
                    null, // callback
                    '', // title
                    'OK' // buttonName
                );
            } else {
                alert('Email already exists.');
            }

            return;

        }
        $scope.category.whatsupAdmins.push($scope.email);
        $scope.update(function (res) {
            $scope.email = '';
            $scope.isValid = false;
        });


    };

    $scope.registerAdmin = function(data){
        var url = '/unifydidentity/user';

        console.log(url);
        $rootScope.callAPI(url, 'POST', data, function (res) {

            var data = res.data;

            $rootScope.firstTimeUser = false;

            $rootScope.user['id'] = data['id'];
            $rootScope.user['profileBackground'] = data['profileBackground'];
            $rootScope.user['firstName'] = data['firstName'];
            $rootScope.user['lastName'] = data['lastName'];
            $rootScope.user['profileImage'] = data['profileImage'];
            $rootScope.user['role'] = data['role'];
            $rootScope.user['tenant'] = data['tenant'];
            $rootScope.user['email'] = data['email'];
            $rootScope.user['phone'] = data['phone'];
            $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
            $rootScope.user['channels'] = data['channels'];
        });
    }


    $scope.registerUser = function () {

        var pref = [];
        for (var i = 0; i < $rootScope.category.length; i++) {
            if (!$rootScope.category[i].rename)
                pref.push($rootScope.category[i].id)
        }

        if (pref.length === 0)
            return;

        console.log(pref);

        var data = {
            firstName: $rootScope.user['firstName'],
            lastName: $rootScope.user['lastName'],
            profileBackground: $rootScope.user['profileBackground'],
            profileImage: $rootScope.user['profileImage'],
            role: $rootScope.user['role'],
            tenant: $rootScope.user['tenant'],
            email: $rootScope.user['email'],
            countryCode: $rootScope.user['countryCode'],
            phone: $rootScope.user['phone'],
            provider: $rootScope.user['provider'],
            gender: $rootScope.user['gender'],
            channels: ['email', 'push']
        };

        if ($rootScope.user['id'])
            data['id'] = $rootScope.user['id'];


        if (!$rootScope.user['areaOfInterest']) {
            console.log('first')
            data['areaOfInterest'] = pref
        } else {

            var update = false;
            console.log('existing')
            for (var i = 0; i < pref.length; i++) {
                if ($rootScope.user['areaOfInterest'].indexOf(pref[i]) === -1) {
                    console.log(pref[i] + ' not added in pref');
                    update = true;
                    break;
                }
            }

            console.log(update);

            if (update)
                data['areaOfInterest'] = pref;
            else
                return;
        }


        /* Getting device push IDs when running on mobile device */
        try {
            if (window.device) {
                var push = PushNotification.init({
                    android: {
                        senderID: "1012473507211"
                    },
                    browser: {
                        pushServiceURL: 'http://push.api.phonegap.com/v1/push'
                    },
                    ios: {
                        alert: "true",
                        badge: "true",
                        sound: "true"
                    },
                    windows: {}
                });

                PushNotification.hasPermission(function(permission) {
                    if (permission.isEnabled) {
                        push.on('registration', function(res) {
                            data.devices = [{
                                "id": res.registrationId,
                                "type": device.platform
                            }];
                            $scope.registerAdmin(data);
                        });
                    } else {
                        $scope.registerAdmin(data);
                    }
                });

                $rootScope.inAppNotificationHandler(push);
            }else {
                $scope.registerAdmin(data);
            }
        } catch (e) {
            alert(e)
        }
    }


    $scope.notificationSettingChange = function (notificationSetting) {
        var keys = Object.keys($scope.category.notificationSetting);

        if (notificationSetting === 'override') {


            for (var i = 0; i < keys.length; i++) {
                if (keys[i] === 'override')
                    continue;

                if (!$scope.category.notificationSetting[keys[i]]) {
                    if (window.device) {
                        navigator.notification.alert(
                            'You can override user setting only when all are enabled.', // message
                            null, // callback
                            '', // title
                            'OK' // buttonName
                        );
                    } else {
                        alert('You can override user setting only when all are enabled.');
                    }

                    $scope.category.notificationSetting.override = !$scope.category.notificationSetting.override;
                    return;

                }

            }
            $scope.update(function (res) {

            })


        } else {

            for (var i = 0; i < keys.length; i++) {
                if (keys[i] === 'override')
                    continue;

                if (!$scope.category.notificationSetting[keys[i]]) {
                    $scope.category.notificationSetting.override = false;
                    break;
                }

            }

            $scope.update(function (res) {

            })

        }
    }

}

function pageprocessorSettings2Page7(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $scope.gender = '';

    var url = '/unifydplatform/gender';

    $rootScope.callAPI(url, 'GET', {}, function (res) {
        $scope.genders = res.data.content;
    });

    $scope.saveGender = function () {
        $rootScope.user['gender'] = $scope.gender;
        $location.path('/app/Settings2/Settings2Page8');
    };

    $scope.genderSelected = function (gender) {
        $scope.gender = gender.gender;
    }

}

function pageprocessorSettings2Page8(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.provider = 'Select provider';
    $scope.phone = '';
    $scope.countryCode = '+1';
    $scope.requestOtp = true;
    $scope.otp = '';
    $scope.providerEmail = '';

    var url = '/unifydplatform/smsprovider';

    $rootScope.callAPI(url, 'GET', {}, function (res) {
        $scope.providers = res.data.content;
    });

    $scope.selectProvider = function (p) {
        $scope.provider = p['provider'];
        $scope.providerEmail = p['email'];
    };
    $scope.isValid = function () {
        return $scope.provider !== 'Select provider' && $scope.phone.toString().length === 10 && !isNaN($scope.phone.toString());
    };


    $scope.requestForOtp = function (isResend) {

        if ($scope.provider === 'Select provider' && $scope.phone.toString().length !== 10 && isNaN($scope.phone.toString()))
            return;

        var url = '/unifyedsms/requestOTP';

        var data = {
            phoneEmail: $scope.phone + "@" + $scope.providerEmail
        };
        $rootScope.callAPI(url, 'POST', data, function (res) {

            if (!isResend)
                $scope.requestOtp = !$scope.requestOtp;
        });

    };

    $scope.savePhone = function () {
        $rootScope.user['phone'] = $scope.phone;
        $rootScope.user['countryCode'] = $scope.countryCode;
        $rootScope.user['provider'] = $scope.providerEmail;

        if ($rootScope.user['areaOfInterest'] && $rootScope.user['areaOfInterest'].length !== 0) {
            var url = '/unifydidentity/user/search/findOneByEmail?email=' + $rootScope.user.email;
            $rootScope.callAPI(url, 'GET', {}, function (res) {
                var user = res.data;

                var url = '/unifydidentity/user/' + user['id'];

                user['gender'] = $rootScope.user['gender'];
                user['phone'] = $rootScope.user['phone'];
                user['provider'] = $rootScope.user['provider'];

                $rootScope.callAPI(url, 'PUT', user, function (res) {

                    $location.path('/app/WhatsUp/WhatsUp');
                    $rootScope.firstTimeUser = false;
                });
            });
        } else if ($rootScope.isUserTenantAdmin) {
            $location.path('/app/Settings2/Settings2Page5');
            $rootScope.firstTimeUser = false;
        } else {
            $location.path('/app/Settings2/Settings2Page4');
        }
    }

    $scope.checkOtp = function () {

        return $scope.otp && $scope.otp.toString().length === 6;
    }

    $scope.verifyForOtp = function () {

        if ($scope.otp.toString().length !== 6)
            return;

        var url = '/unifyedsms/verifyOTP';

        var data = {
            phoneEmail: $scope.phone + "@" + $scope.providerEmail,
            otp: $scope.otp
        };
        $rootScope.callAPI(url, 'POST', data, function (res) {
            if (res.data == 'true')
                $scope.savePhone();
            else {
                if (window.device) {
                    navigator.notification.alert("Please enter valid OTP", null, ' ', 'Ok');
                } else {
                    //alert("Invalid youtube url.");
                    $.alert({
                        animation: 'none',
                        title: '',
                        content: 'Please enter valid OTP',
                        columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
                        buttons: {
                            OK: {
                                'btnClass': 'btn-primary boxradius popBtns',
                                'action': function () {
                                    //alert('cancel');
                                }
                            }
                        }
                    });
                }

            }
        });
    }
}

function pageprocessorSettings2Page9(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);


    try {

        if ((!$rootScope.user['gender'] || !$rootScope.user['phone']) && $rootScope.user.products.indexOf('messaging') !== -1) {
            /*
            * If gender or phone is missing and tenant has messaging applet, then redirect to Select Gender page
            * */
            $rootScope.firstTimeUser = true;
            $location.path('/app/Settings2/Settings2Page7');

        } else if (!$rootScope.user['areaOfInterest']) {
            if ($rootScope.isUserTenantAdmin) {
                $location.path('/app/Settings2/Settings2Page5');
                $rootScope.firstTimeUser = false;
            } else {
                $rootScope.firstTimeUser = true;
                $location.path('/app/Settings2/Settings2Page4');
            }

        } else {
            $rootScope.firstTimeUser = false;
            $location.path('/app/WhatsUp/WhatsUp');
        }

    } catch (e) {
        console.log(e);
    }

}
