function pageprocessorEDUQDial(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
$("#fixedFooterContent").html('');
  $scope.quickdial = pagedef.quickdialnumbers;
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1); 
  $scope.quickdial = $scope.quickdial.sort(sort_by('name', false, null));
  $scope.initialNames = function(qd) {
    return qd['name'].substr(0, 1);
  };
  function sort_by(field, reverse, primer) {
    var key = primer ?
      function(x) {
        return primer(x[field]);
      } : function(x) {
        return x[field];
      };

    reverse = reverse ? -1 : 1;

    return function(a, b) {
      return a = key(a), b = key(b), reverse * ((a > b) - (b > a));
    };
  };
}
