function pageprocessorAthletics187(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    var url = 'https://kryptosmw.kryptosmobile.com/webadapter3/services/public/lsuunifyed/LSU/LSU_ATHLETICS';
    $.blockUI();
    $http.get(url).success(function (data, status, headers, config) {
        try {
            for (var i = 0; i < data.links.length; i++) {
                data.links[i]['date'] = new Date(data.links[i]['time']);
            }
        } catch (e) {
        }

        $scope.feeds = data.links;
        $rootScope.Athletics168feeds = data.links;
        $.unblockUI();
    }).error(function (error, status, header, config) {
        $.unblockUI();
    });
    $scope.openFeed = function (index) {
        $rootScope.Athletics168feedDetails = $scope.feeds[index];
        $rootScope.Athletics168Index = index;
        $location.path("/app/Athletics187/Athletics187Page2");
    };

}

function pageprocessorAthletics187Page2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.feedDetails = $rootScope.Athletics168feedDetails;
    $scope.index = $rootScope.Athletics168Index;

    $scope.openWebPage = function (url) {
        window.open(url, "_blank", "location=no");
    };
    $scope.trustHTML = function (html) {
        var data = "<div>" + html + "</div>";
        return $sce.trustAsHtml(data);
    };

    $scope.back = function () {
        window.history.back();
    }

    $scope.next = function () {

        if ($scope.index === $rootScope.Athletics168feeds.length - 1)
            return;

        $scope.index++;
        $scope.feedDetails = $rootScope.Athletics168feeds[$scope.index];

    };
    $scope.prev = function () {

        if ($scope.index === 0)
            return;

        $scope.index--;
        $scope.feedDetails = $rootScope.Athletics168feeds[$scope.index];

    }
}

